requests.tests:
eval(postman.getGlobalVariable('postmanBDD'));
 
describe('Create Amount Discount Campaign with messageChannelType is invalid', function() {
    it('should return HTTP status 422 (Unprocessable Entity) when creating an Amount discount campaign fixed amount with messageChannelType is invalid', function () {
       response.should.have.status(422);
       response.should.be.json;
       response.body.should.not.be.empty;
       
    });
 
    it('Create Amount Discount Campaign with messageChannelType is invalid, response body should match all entity and value', function () {
      var campaign = response.body[0];
      campaign.should.be.an('object');
      campaign.should.have.property('errorCode').and.equal('UNKNOWN_FIELD_VALUE');
      campaign.should.have.property('field').and.equal('driverAndBenefitsDetails.amountDriverAndBenefitsDetails.amountDriverScenariosAndBenefitsDetails[0].scenarioMessageDetails.messageChannelType');
      campaign.should.have.property('originalValue').and.eql(['POS']);
      campaign.should.have.property('errorMessage').and.equal('No Message channel type of POS found');
      campaign.should.have.property('helpUrl').and.equal('https://knowledgehub.collinsongroup.com/display/WRT/04+-+Business+Error+Responses+on+APIs/v1/errors/UNKNOWN_FIELD_VALUE#driverAndBenefitsDetails.amountDriverAndBenefitsDetails.amountDriverScenariosAndBenefitsDetails[0].scenarioMessageDetails.messageChannelType');
      
    });

});

