describe('Create Amount Discount Campaign with camapign name field is more than 24 characters', function() {
    it('should return HTTP status 422 Unprocessable Entity when creating an Amount Discount Campaign with camapign name field is more than 24 characters in the request', function () {
       response.should.have.status(422);
       response.should.be.json;
       response.body.should.not.be.empty;
       
    });
 
    it('Create Amount Discount Campaign with camapign name field is more than 24 characters, response body should match all entity and value', function () {
      var campaign = response.body[0];
      campaign.should.be.an('object');
      campaign.should.have.property('errorCode').and.equal('INVALID_FIELD_LENGTH');
      campaign.should.have.property('field').and.equal('name');
      campaign.should.have.property('originalValue').and.equal('7$ OFF amount discounts 7');
      campaign.should.have.property('errorMessage').and.equal('Name maximum length is 24 characters');
      campaign.should.have.property('helpUrl').and.equal('https://knowledgehub.collinsongroup.com/display/WRT/04+-+Business+Error+Responses+on+APIs/v1/errors/INVALID_FIELD_LENGTH#name');
      
    });

});

