describe('Create Amount Discount Campaign with ownerType is missing', function() {
    it('should return HTTP status 422 Unprocessable Entity when creating an Amount Discount Campaign with ownerType is missing in the request', function () {
       response.should.have.status(422);
       response.should.be.json;
       response.body.should.not.be.empty;
       
    });
 
    it('Create Amount Discount Campaign with ownerType is missing, response body should match all entity and value', function () {
      var campaign = response.body[0];
      campaign.should.be.an('object');
      campaign.should.have.property('errorCode').and.equal('MISSING_FIELD_VALUE');
      campaign.should.have.property('field').and.equal('ownerType');
      campaign.should.have.property('originalValue').and.equal('');
      campaign.should.have.property('errorMessage').and.equal('Owner type is required');
      campaign.should.have.property('helpUrl').and.equal('https://knowledgehub.collinsongroup.com/display/WRT/04+-+Business+Error+Responses+on+APIs/v1/errors/MISSING_FIELD_VALUE#ownerType');
      
    });

});

