describe('Create Amount Discount Campaign when min purchaseAmount is negative "-10"', function() {
    it('should return HTTP status 422 Unprocessable Entity when creating an Amount Discount Campaign when min purchaseAmount is negative "-10"', function () {
       response.should.have.status(422);
       response.should.be.json;
       response.body.should.not.be.empty;
       
    });
 
    it('Create Amount Discount Campaign when min purchaseAmount is negative "-10" in response body should match all entity and value', function () {
      var campaign = response.body[0];
      campaign.should.be.an('object');
      campaign.should.have.property('errorCode').and.equal('INVALID_FIELD_VALUE');
      campaign.should.have.property('field').and.equal('minimumPurchaseAmount');
      campaign.should.have.property('originalValue').and.equal(-10);
      campaign.should.have.property('errorMessage').and.equal('Incorrect format - minimum purchase amount cannot be less than 0.01');
      campaign.should.have.property('helpUrl').and.equal('https://knowledgehub.collinsongroup.com/display/WRT/04+-+Business+Error+Responses+on+APIs/v1/errors/INVALID_FIELD_VALUE#minimumPurchaseAmount');
      
    });

});

