eval(postman.getGlobalVariable('postmanBDD'));
 
describe('Compute campaign statistics with rollUpPeriodType field is invalid', function() {
    it('should return HTTP status 422 Unprocessable Entity when Compute campaign statistics with rollUpPeriodType field is invalid in the request', function () {
       response.should.have.status(422);
       response.should.be.json;
       response.body.should.not.be.empty;
       
    });
 
    it('Compute campaign statistics with rollUpPeriodType field is invalid, response body should match all entity and value', function () {
      var campaign = response.body[0];
      campaign.should.be.an('object');
      campaign.should.have.property('errorCode').and.equal('UNKNOWN_FIELD_VALUE');
      campaign.should.have.property('field').and.equal('rollUpPeriodType');
      campaign.should.have.property('originalValue').and.equal('Per Century');
      campaign.should.have.property('errorMessage').and.equal('No roll up period type of Per Century found');
      campaign.should.have.property('helpUrl').and.equal('https://knowledgehub.collinsongroup.com/display/WRT/04+-+Business+Error+Responses+on+APIs/v1/errors/UNKNOWN_FIELD_VALUE#rollUpPeriodType');
      
    });

});

